import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [numbers, setNumbers] = useState([0, 0, 0, 0, 0]);
  const [result, setResult] = useState(null);

  const handleInputChange = (index, value) => {
    const updatedNumbers = [...numbers];
    updatedNumbers[index] = parseInt(value);
    setNumbers(updatedNumbers);
  };

  const handleCalculate = async (endpoint) => {
    try {
      const response = await axios.post(endpoint, numbers, {
        headers: {
          'Content-Type': 'application/json'
        }
      });

      setResult(response.data.result); // Assuming the API response has a "result" field
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="App">
      {/* Input areas */}
      {numbers.map((number, index) => (
        <input
          key={index}
          type="number"
          placeholder={`Enter number ${index + 1}`}
          value={number}
          onChange={(e) => handleInputChange(index, e.target.value)}
        />
      ))}

      {/* Buttons for calculations */}
      <div>
        <button onClick={() => handleCalculate('http://localhost:5264/numbers/add')}>
          Add Numbers
        </button>
        <button onClick={() => handleCalculate('http://localhost:5264/numbers/average')}>
          Average Numbers
        </button>
        <button onClick={() => handleCalculate('http://localhost:5264/numbers/lowest')}>
          Find Lowest
        </button>
        <button onClick={() => handleCalculate('http://localhost:5264/numbers/highest')}>
          Find Highest
        </button>
        <button onClick={() => handleCalculate('http://localhost:5264/numbers/difference')}>
          Find Range Difference
        </button>
      </div>

      {/* Display the result */}
      {result !== null && <p>Result: {result}</p>}
    </div>
  );
}

export default App;
