import axios from 'axios';

// API endpoint URL
const apiUrl = 'http://localhost:5264/numbers';

// Endpoint paths
const addEndpoint = `${apiUrl}/add`;
const averageEndpoint = `${apiUrl}/average`;
const lowestEndpoint = `${apiUrl}/lowest`;
const highestEndpoint = `${apiUrl}/highest`;
const differenceEndpoint = `${apiUrl}/difference`;

// Example function to send requests using axios
async function sendRequest(endpoint, numbers) {
  try {
    const response = await axios.post(endpoint, numbers, {
      headers: {
        'Content-Type': 'application/json'
      }
    });
    return response.data.result;
  } catch (error) {
    console.error('Error:', error);
    throw error; // Re-throw the error to handle it in the component
  }
}

export {
  addEndpoint,
  averageEndpoint,
  lowestEndpoint,
  highestEndpoint,
  differenceEndpoint,
  sendRequest,
};
