import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [numbers, setNumbers] = useState([]);
  const [result, setResult] = useState(null);

  const apiUrl = 'http://your-server-url/numbers';
  const endpoints = {
    add: `${apiUrl}/add`,
    average: `${apiUrl}/average`,
    lowest: `${apiUrl}/lowest`,
    highest: `${apiUrl}/highest`,
    difference: `${apiUrl}/difference`,
  };

  const handleCalculate = async (endpoint) => {
    try {
      const response = await axios.post(endpoints[endpoint], numbers, {
        headers: {
          'Content-Type': 'application/json'
        }
      });

      setResult(response.data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="App">
      {/* Input areas */}
      <div>
        <input
          type="number"
          placeholder="Enter number 1"
          onChange={(e) => setNumbers([parseInt(e.target.value), ...numbers.slice(1)])}
        />
        {/* Repeat similar input areas for numbers 2 to 5 */}
      </div>

      {/* Buttons for calculations */}
      <div>
        <button onClick={() => handleCalculate('add')}>Add Numbers</button>
        <button onClick={() => handleCalculate('average')}>Average Numbers</button>
        <button onClick={() => handleCalculate('lowest')}>Find Lowest</button>
        <button onClick={() => handleCalculate('highest')}>Find Highest</button>
        <button onClick={() => handleCalculate('difference')}>Find Range Difference</button>
      </div>

      {/* Display the result */}
      {result !== null && <p>Result: {result}</p>}
    </div>
  );
}

export default App;
